mapper.select:
select g.*, c.name as categoryName from goods g left join category c on g.category_id = c.id


        and d.name like concat('%',#{name},'%')


        and d.category_id = #{categoryId}


        and d.status = #{status}


order by d.create_time desc

mapper.update:
update goods
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status}, update_at = #{updateAt
where id = #{id}

mapper.select:
select * from goods


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}


order by create_time desc

select count(id) from goods


        and status = #{status}


        and category_id = #{categoryId}

select * from goods where

    category_id = #{categoryId}

and status = #{status}
